(5168, 'en', 'default', 'd0e80edbec66288a957ad0381ae03505', '<code>_top</code> &mdash; current window or tab, with no frames.', NULL, NULL, 'LINE', NULL, 10, '', 'eacc121dfd228bdf407e10172ae29704', NULL, ''),
(5169, 'en', 'default', '747ebf74423d5239b4023bc329eaf353', '<span>Active:</span> %s', NULL, NULL, 'LINE', NULL, 10, '', '14fb66009c7a8b6f0cf5e1ded911823b', NULL, ''),
(5170, 'en', 'default', '2e9457fe36435e48a296c4a614e71a4f', '<strong>%s</strong> &mdash; This allows you to associate an image with your post without inserting it. This is usually useful only if your theme makes use of the image as a post thumbnail on the home page, a custom header, etc.', NULL, NULL, 'LINE', NULL, 10, '', '3006d7934608817a28a556e753065609', NULL, ''),
(5171, 'en', 'default', 'f067f6f2a07a9f28b8acd051de12bd8f', '<strong><em>Note that password</em></strong> carefully! It is a <em>random</em> password that was generated just for you.', NULL, NULL, 'LINE', NULL, 10, '', '667f6a0fa877d898323aca1fc507a99e', NULL, ''),
(5172, 'en', 'default', 'ecdb52fb00d5aabe4e9d24b436f9b9fc', '<strong>Activity</strong> &mdash; Shows the upcoming scheduled posts, recently published posts, and the most recent comments on your posts and allows you to moderate them.', NULL, NULL, 'LINE', NULL, 10, '', '92492a427dfb72cd1b97719e8e35e896', NULL, ''),
(5173, 'en', 'default', 'a6babfcb475f676a839ed6c0640d1a7d', '<strong>Advice:</strong> think very carefully about your site crashing if you are live-editing the theme currently in use.', NULL, NULL, 'LINE', NULL, 10, '', 'ad4647305c60cfd93de5e674fc745759', NULL, ''),
(5174, 'en', 'default', 'a8f4d14621490c3a1b94015a6744f684', '<strong>At A Glance</strong> &mdash; Displays a summary of the content on your site and identifies which theme and version of WordPress you are using.', NULL, NULL, 'LINE', NULL, 10, '', '63cb0e79249a3955ef8698cb0ff6b575', NULL, ''),
(5175, 'en', 'default', 'b65fa8d75f92ea10c226c9c9e107cfc6', '<strong>Box Controls</strong> &mdash; Click the title bar of the box to expand or collapse it. Some boxes added by plugins may have configurable content, and will show a &#8220;Configure&#8221; link in the title bar if you hover over it.', NULL, NULL, 'LINE', NULL, 10, '', '54545fe38eda999a7a67927af2e0e559', NULL, ''),
(5176, 'en', 'default', '2bc2b92ef21e54b7d57719af3e03b906', '<strong>Clicking the arrow to the right of any menu item</strong> in the editor will reveal a standard group of settings. Additional settings such as link target, CSS classes, link relationships, and link descriptions can be enabled and disabled via the Screen Options tab.', NULL, NULL, 'LINE', NULL, 10, '', '44adfce5db156b9d67e7c275453a89b9', NULL, ''),
(5177, 'en', 'default', 'b1223bca091660d2cb26c4403f354ea0', '<strong>Compatible</strong> with your version of WordPress', NULL, NULL, 'LINE', NULL, 10, '', '96c7d1203469aa395da152a984342fe8', NULL, ''),
(5178, 'en', 'default', '019360949341be40f9abc62e0d947806', '<strong>Connection lost.</strong> Saving has been disabled until you&#8217;re reconnected.', NULL, NULL, 'LINE', NULL, 10, '', 'c7ad62cbc8e82263e1ecdbb66fe5fb52', NULL, ''),
(5179, 'en', 'default', '009d122ea5b480829aa49478250882f2', '<strong>Delete</strong> brings you to the Delete Users screen for confirmation, where you can permanently remove a user from your site and delete their content. You can also delete multiple users at once by using Bulk Actions.', NULL, NULL, 'LINE', NULL, 10, '', '6f347940b95b62848846a02047a935e5', NULL, ''),
(5180, 'en', 'default', '455ea6f84b4f4e0349d551dbb50d3a42', '<strong>Description</strong> &mdash; The description is not prominent by default; however, some themes may display it.', NULL, NULL, 'LINE', NULL, 10, '', 'bf0e83818cfea660dd6082860a6da784', NULL, ''),
(5181, 'en', 'default', '72a914bd12c78373873416f57b033b13', '<strong>Discussion</strong> &mdash; You can turn comments and pings on or off, and if there are comments on the post, you can see them here and moderate them.', NULL, NULL, 'LINE', NULL, 10, '', 'feb178d959aae886b2dde85248492051', NULL, ''),
(5182, 'en', 'default', '71ce1121b4c5579d8f2c8e71335b9a22', '<strong>Drag and Drop</strong> &mdash; To rearrange the boxes, drag and drop by clicking on the title bar of the selected box and releasing when you see a gray dotted-line rectangle appear in the location you want to place the box.', NULL, NULL, 'LINE', NULL, 10, '', '5152a8dbc58f8c3531ec3b414be417aa', NULL, ''),
(5183, 'en', 'default', 'eb233701305e7a3f2c7906f8fc65e817', '<strong>Drag and drop</strong> your files into the area below. Multiple files are allowed.', NULL, NULL, 'LINE', NULL, 10, '', '8edfe8913a0e716cd74f3f38806c7700', NULL, ''),
(5184, 'en', 'default', 'f62a6b681a08721ae6b0f640c11373e9', '<strong>ERROR:</strong> There was an error connecting to the server, Please verify the settings are correct.', NULL, NULL, 'LINE', NULL, 10, '', '4de9a113fcc64567a124a76f81341274', NULL, ''),
(5185, 'en', 'default', '7d150f25e6563473ebc64666237efb54', '<strong>ERROR</strong>: \"Table Prefix\" is invalid.', NULL, NULL, 'LINE', NULL, 10, '', 'a8b0f7cfdec390a2a79211fa262fccd6', NULL, ''),
(5186, 'en', 'default', '881815006609d34e07215728019253f8', '<strong>ERROR</strong>: Passwords may not contain the character \"\\\".', NULL, NULL, 'LINE', NULL, 10, '', 'b8a94917e225393c809afc968a530c56', NULL, ''),
(5187, 'en', 'default', '7015bce91212ccb97c01670ea4114bdb', '<strong>ERROR</strong>: Please enter a nickname.', NULL, NULL, 'LINE', NULL, 10, '', '90b0199963b37d6472a3ed4b7ae18e1f', NULL, ''),
(5188, 'en', 'default', 'bea96aebb50c876c1a95f41c49a3f3c9', '<strong>ERROR</strong>: Please enter a password.', NULL, NULL, 'LINE', NULL, 10, '', 'e8c3c68119915112d4130386ab6bb97d', NULL, ''),
(5189, 'en', 'default', 'd15b5951c64392d97ca1e69fbd5d974e', '<strong>ERROR</strong>: Please enter an email address.', NULL, NULL, 'LINE', NULL, 10, '', 'e9ef1e8b9360dfae53505fe12b26e711', NULL, ''),
(5190, 'en', 'default', 'ee9cd024fec5f5c83e08caabf9662327', '<strong>ERROR</strong>: Please enter the same password in both password fields.', NULL, NULL, 'LINE', NULL, 10, '', '89b1f6507cedd33fe4572fda596030b1', NULL, ''),
(5191, 'en', 'default', '044f46e52ac3d0ba7f6b83fc33fc1b9d', '<strong>ERROR</strong>: options page not found.', NULL, NULL, 'LINE', NULL, 10, '', 'cfde005e3ca61e0c36826e8c152eb5e4', NULL, ''),
(5192, 'en', 'default', '9b0ce93b03db1e1cd98decc6387fa90c', '<strong>Edit</strong> takes you to the editable profile screen for that user. You can also reach that screen by clicking on the username.', NULL, NULL, 'LINE', NULL, 10, '', '11696b2610f2bbca1738397defd5ba29', NULL, ''),
(5193, 'en', 'default', '0bdd62cf14b1ac2df851e97eed7faa8c', '<strong>Edit</strong> takes you to the editing screen for that post. You can also reach that screen by clicking on the post title.', NULL, NULL, 'LINE', NULL, 10, '', '74deb9541548900791736df7e47727d1', NULL, ''),
(5194, 'en', 'default', '1d667c275a75b28bd9a3d3befcc8d270', '<strong>Format</strong> &mdash; Post Formats designate how your theme will display a specific post. For example, you could have a <em>standard</em> blog post with a title and paragraphs, or a short <em>aside</em> that omits the title and contains a short text blurb. Please refer to the Codex for <a href=\"https://codex.wordpress.org/Post_Formats#Supported_Formats\">descriptions of each post format</a>. Your theme could enable all or some of 10 possible formats.', NULL, NULL, 'LINE', NULL, 10, '', 'a802c479a057bc8c8d3892f320b37b76', NULL, ''),
(5195, 'en', 'default', 'd7cb0009b8a18e9d23b53e25112dad2a', '<strong>Important:</strong> before updating, please <a href=\"https://codex.wordpress.org/WordPress_Backups\">back up your database and files</a>. For help with updates, visit the <a href=\"https://codex.wordpress.org/Updating_WordPress\">Updating WordPress</a> Codex page.', NULL, NULL, 'LINE', NULL, 10, '', '6ed87801b66365a3f38d73cebc5f0f41', NULL, ''),
(5196, 'en', 'default', 'adc736d6139e1287f1d9baaf718f5522', '<strong>Incompatible</strong> with your version of WordPress', NULL, NULL, 'LINE', NULL, 10, '', '13e0073401d88b673152424e5da67e61', NULL, ''),
(5197, 'en', 'default', '996b2b4aa8413fcd0f6d148b2af946a6', '<strong>Info</strong> &mdash; The site URL is rarely edited as this can cause the site to not work properly. The Registered date and Last Updated date are displayed. Network admins can mark a site as archived, spam, deleted and mature, to remove from public listings or disable.', NULL, NULL, 'LINE', NULL, 10, '', '3d34da6684f8784cc40405ae4bc6550d', NULL, ''),
(5198, 'en', 'default', '1c8ad85a1a29170a2c98285371e4ac2d', '<strong>Name</strong> &mdash; The name is how it appears on your site.', NULL, NULL, 'LINE', NULL, 10, '', '5febbe59b4e8c2b38e143b3cff8ae190', NULL, ''),
(5199, 'en', 'default', 'dac04065919539b3bb81eb4152251ca4', '<strong>Order</strong> &mdash; Pages are usually ordered alphabetically, but you can choose your own order by entering a number (1 for first, etc.) in this field.', NULL, NULL, 'LINE', NULL, 10, '', 'f11c47af7f98788ea05b855c41902aa2', NULL, ''),
(5200, 'en', 'default', '4ea8e185f297fa1e5706a8337719effe', '<strong>Parent</strong> &mdash; Categories, unlike tags, can have a hierarchy. You might have a Jazz category, and under that have child categories for Bebop and Big Band. Totally optional. To create a subcategory, just choose another category from the Parent dropdown.', NULL, NULL, 'LINE', NULL, 10, '', 'eec619c60b3b0a3e38f0069c120d2602', NULL, ''),
(5201, 'en', 'default', '8f6225bd7662575ef84d375fec88dbd4', '<strong>Parent</strong> &mdash; You can arrange your pages in hierarchies. For example, you could have an &#8220;About&#8221; page that has &#8220;Life Story&#8221; and &#8220;My Dog&#8221; pages under it. There are no limits to how many levels you can nest pages.', NULL, NULL, 'LINE', NULL, 10, '', 'a7d363674bc0d711d6d160e3dbf13846', NULL, ''),
(5202, 'en', 'default', 'd1bb204120db5310aec8f2490431cb76', '<strong>Please Note:</strong> Any customizations you have made to theme files will be lost. Please consider using <a href=\"%s\">child themes</a> for modifications.', NULL, NULL, 'LINE', NULL, 10, '', '04573092292e1d20b219c5d37cabb34e', NULL, ''),
(5203, 'en', 'default', '91cc965a84f790ad2906d697f3119a1d', '<strong>Post editor</strong> &mdash; Enter the text for your post. There are two modes of editing: Visual and Text. Choose the mode by clicking on the appropriate tab.', NULL, NULL, 'LINE', NULL, 10, '', 'a0bfc377e504c07ae3b7b4ee7eb27344', NULL, ''),
(5204, 'en', 'default', 'b807523847b38ac8553394a6e915ef62', '<strong>Preview</strong> will show you what your draft post will look like if you publish it. View will take you to your live site to view the post. Which link is available depends on your post&#8217;s status.', NULL, NULL, 'LINE', NULL, 10, '', 'feb9ddd4b67850aee6a2151805f1dfa8', NULL, ''),
(5205, 'en', 'default', 'ad831971b7a4b43e2ef0d627e297902a', '<strong>Publish</strong> &mdash; You can set the terms of publishing your post in the Publish box. For Status, Visibility, and Publish (immediately), click on the Edit link to reveal more options. Visibility includes options for password-protecting a post or making it stay at the top of your blog indefinitely (sticky). The Password protected option allows you to set an arbitrary password for each post. The Private option hides the post from everyone except editors and administrators. Publish (immediately) allows you to set a future or past date and time, so you can schedule a post to be published in the future or backdate a post.', NULL, NULL, 'LINE', NULL, 10, '', 'ffea9d2f9ee57d2e3ab4a669c39607c5', NULL, ''),
(5206, 'en', 'default', '78bf51b73e62500109ff97739c24bb7d', '<strong>Quick Draft</strong> &mdash; Allows you to create a new post and save it as a draft. Also displays links to the 5 most recent draft posts you\'ve started.', NULL, NULL, 'LINE', NULL, 10, '', 'ccbc678b1234e3865ff243cf2f5e033b', NULL, ''),
(5207, 'en', 'default', '099f3df0632488f2c47e2658d95e0e76', '<strong>Quick Edit</strong> provides inline access to the metadata of your post, allowing you to update post details without leaving this screen.', NULL, NULL, 'LINE', NULL, 10, '', 'aec7db39f9755f7c2dc6e2b37c8412c2', NULL, ''),
(5208, 'en', 'default', 'b7afe14ef93e550358d9308fbcdf9df0', '<strong>Random:</strong> Show a different image on each page.', NULL, NULL, 'LINE', NULL, 10, '', 'ba08a9ca69ff15ee804b268fc9029e72', NULL, ''),
(5209, 'en', 'default', 'a7e4d48c9f714a92b5b5beb3db05ad38', '<strong>Remove</strong> allows you to remove a user from your site. It does not delete their content. You can also remove multiple users at once by using Bulk Actions.', NULL, NULL, 'LINE', NULL, 10, '', '5fe47707a48d7746511ccf9843929025', NULL, ''),
(5210, 'en', 'default', 'a98be58ec342822c43f9a60f423b1d2f', '<strong>Screen Options</strong> &mdash; Use the Screen Options tab to choose which Dashboard boxes to show.', NULL, NULL, 'LINE', NULL, 10, '', 'ed407f0af001c6226b4cc9be2875d701', NULL, ''),
(5211, 'en', 'default', 'd6ce148cfd01c369f1b23045e489b491', '<strong>Send Trackbacks</strong> &mdash; Trackbacks are a way to notify legacy blog systems that you&#8217;ve linked to them. Enter the URL(s) you want to send trackbacks. If you link to other WordPress sites they&#8217;ll be notified automatically using pingbacks, and this field is unnecessary.', NULL, NULL, 'LINE', NULL, 10, '', '0671b4a2f6125c4f545933974afb647d', NULL, ''),
(5212, 'en', 'default', '0d7d65c866eb8c69ac9cb51558adfa14', '<strong>Settings</strong> &mdash; This page shows a list of all settings associated with this site. Some are created by WordPress and others are created by plugins you activate. Note that some fields are grayed out and say Serialized Data. You cannot modify these values due to the way the setting is stored in the database.', NULL, NULL, 'LINE', NULL, 10, '', 'd354fd7e8af531c92dc88db3ef74cfe4', NULL, ''),
(5213, 'en', 'default', '411d40a6a21e11c47ed8adb1c92f00b0', '<strong>Slug</strong> &mdash; The &#8220;slug&#8221; is the URL-friendly version of the name. It is usually all lowercase and contains only letters, numbers, and hyphens.', NULL, NULL, 'LINE', NULL, 10, '', '97af8d8b956434fb602f37b6e0e53e27', NULL, ''),
(5214, 'en', 'default', '597ef2b39aedb4e7f14e838eed92b8f7', '<strong>Template</strong> &mdash; Some themes have custom templates you can use for certain pages that might have additional features or custom layouts. If so, you&#8217;ll see them in this dropdown menu.', NULL, NULL, 'LINE', NULL, 10, '', '62319763264b08b9688c6a9f025316ba', NULL, ''),
(5215, 'en', 'default', 'ce993a5f75691ce7176e73214153f067', '<strong>The parent theme could not be found.</strong> You will need to install the parent theme, %s, before you can use this child theme.', NULL, NULL, 'LINE', NULL, 10, '', 'b7bcb279eab6de0e2f5af6b67f2c9a78', NULL, ''),
(5216, 'en', 'default', '83898cec1dbe4f4586c8347c1ba46120', '<strong>Themes and Plugins</strong> &mdash; To update individual themes or plugins from this screen, use the checkboxes to make your selection, then <strong>click on the appropriate &#8220;Update&#8221; button</strong>. To update all of your themes or plugins at once, you can check the box at the top of the section to select all before clicking the update button.', NULL, NULL, 'LINE', NULL, 10, '', '98ac0766c2d6280e0f1ccde117bde100', NULL, ''),
(5217, 'en', 'default', 'b23e308c3f8aaf77e0abb955c2b42721', '<strong>Themes</strong> &mdash; This area shows themes that are not already enabled across the network. Enabling a theme in this menu makes it accessible to this site. It does not activate the theme, but allows it to show in the site&#8217;s Appearance menu. To enable a theme for the entire network, see the <a href=\"%s\">Network Themes</a> screen.', NULL, NULL, 'LINE', NULL, 10, '', 'e27f32c9e1231c938c359bd433295481', NULL, ''),
(5218, 'en', 'default', '2d08934c32876d0e82311f1741b4e5cb', '<strong>Title</strong> &mdash; Enter a title for your post. After you enter a title, you&#8217;ll see the permalink below, which you can edit.', NULL, NULL, 'LINE', NULL, 10, '', 'caebf199686f82f701abc47dd032baad', NULL, ''),
(5219, 'en', 'default', 'da5abbe81c1b1941c214b1b4298822cb', '<strong>Translations</strong> &mdash; The files translating WordPress into your language are updated for you whenever any other updates occur. But if these files are out of date, you can <strong>click the &#8220;Update Translations&#8221;</strong> button.', NULL, NULL, 'LINE', NULL, 10, '', 'd5ff3112c75a90a53ba4a28d043eb4a7', NULL, ''),
(5220, 'en', 'default', '1ac12183049e2cd887bb8ee6b488d5fb', '<strong>Trash</strong> removes your post from this list and places it in the trash, from which you can permanently delete it.', NULL, NULL, 'LINE', NULL, 10, '', 'fb4aa500e080edf20572554e14786e36', NULL, ''),
(5221, 'en', 'default', '55c1d7dcd5c13ba5790cecd4c6cbd55f', '<strong>Users</strong> &mdash; This displays the users associated with this site. You can also change their role, reset their password, or remove them from the site. Removing the user from the site does not remove the user from the network.', NULL, NULL, 'LINE', NULL, 10, '', '97df9b3590027eed340d84b2abc5d08c', NULL, ''),
