(5871, 'en', 'default', 'c7b428708659c3c01c911d65bb3ebe23', 'If you don&#8217;t want a header image to be displayed on your site at all, click the &#8220;Remove Header Image&#8221; button at the bottom of the Header Image section of this page. If you want to re-enable the header image later, you just have to select one of the other image options and click &#8220;Save Changes&#8221;.', NULL, NULL, 'LINE', NULL, 10, '', 'd40e2cf9e52c77edd1824c1dd7db77b9', NULL, ''),
(5872, 'en', 'default', '630df4be582006585fc90ce4b93f9254', 'If you don&lsquo;t want to upload your own image, you can use one of these cool headers, or show a random one.', NULL, NULL, 'LINE', NULL, 10, '', '670f09b60167f2151c58a9aac9220ebf', NULL, ''),
(5873, 'en', 'default', '62c4541e6538f354865d8026c67d446e', 'If you experience any issues or need support, the volunteers in the WordPress.org support forums may be able to help.', NULL, NULL, 'LINE', NULL, 10, '', 'eeb98b73b769919deb0e8a7cb490efea', NULL, ''),
(5874, 'en', 'default', '3e0109b6197e41063cfac796836f2dad', 'If you have a plugin in a .zip format, you may install it by uploading it here.', NULL, NULL, 'LINE', NULL, 10, '', 'e095159d4751cb7d3f7fb57962f8b003', NULL, ''),
(5875, 'en', 'default', '499ba6a1e9428f7283cd1405774270d1', 'If you have a theme in a .zip format, you may install it by uploading it here.', NULL, NULL, 'LINE', NULL, 10, '', 'b0325cbc30aaa9852894a4fd7f344d8e', NULL, ''),
(5876, 'en', 'default', '62f7387a9ef151c294e680b2fd42169e', 'If you have marked plugins as favorites on WordPress.org, you can browse them here.', NULL, NULL, 'LINE', NULL, 10, '', 'b8fec56cb45e1a17dc0a62f2b168910f', NULL, ''),
