INSERT INTO `arch_icl_strings` (`id`, `language`, `context`, `name`, `value`, `string_package_id`, `location`, `type`, `title`, `status`, `gettext_context`, `domain_name_context_md5`, `word_count`, `translation_priority`) VALUES
(5879, 'en', 'default', 'c5ecefc2c05db6570556c7385ac2e8a3', 'If you haven&#8217;t yet created any menus, <strong>click the &#8217;create a new menu&#8217; link</strong> to get started', NULL, NULL, 'LINE', NULL, 10, '', 'e3750bb5eb2eb05b624dbab6c20b5e59', NULL, ''),
(5880, 'en', 'default', '2448f34ad2e38271ffbcf4d299c17c5d', 'If you just want to get an idea of what&#8217;s available, you can browse Featured and Popular plugins by using the links above the plugins list. These sections rotate regularly.', NULL, NULL, 'LINE', NULL, 10, '', '575aca988849707fdbbfd60be15902e1', NULL, ''),
(5881, 'en', 'default', 'a379ef32870b64a6ef175d6e3da0bf17', 'If you know what you&#8217;re looking for, Search is your best bet. The Search screen has options to search the WordPress Plugin Directory for a particular Term, Author, or Tag. You can also search the directory by selecting popular tags. Tags in larger type mean more plugins have been labeled with that tag.', NULL, NULL, 'LINE', NULL, 10, '', '2dfd3ebb5bbf8256c5c0cc8fefb8b3c8', NULL, ''),
(5882, 'en', 'default', '864e67dda7ff0dc96db5c841bdbf98ad', 'If you like, you may enter custom structures for your category and tag URLs here. For example, using <code>topics</code> as your category base would make your category links like <code>%s/topics/uncategorized/</code>. If you leave these blank the defaults will be used.', NULL, NULL, 'LINE', NULL, 10, '', 'd635a782b4c7393661373e724751b36c', NULL, ''),
(5883, 'en', 'default', '837a1e84e411703eb806ddee77465493', 'If you need to re-install version %s, you can do so here:', NULL, NULL, 'LINE', NULL, 10, '', '05757d13169fcca8a5f39dae14249346', NULL, ''),
(5884, 'en', 'default', '1a89b9744e617ef3fb4ab7c4fffee90c', 'If you pick an option other than Plain, your general URL path with structure tags (terms surrounded by <code>%</code>) will also appear in the custom structure field and your path can be further modified there.', NULL, NULL, 'LINE', NULL, 10, '', '8a085eced0d0591c402c82cff68cbc18', NULL, ''),
(5885, 'en', 'default', '97b78ef138769a3fcf7e03efeafe2005', 'If you reach out to us, we\'ll also ensure you\'ll never have this problem again.', NULL, NULL, 'LINE', NULL, 10, '', 'cea417fb3ad33a8c6e47bd88db6f8df1', NULL, ''),
(5886, 'en', 'default', 'b1547fa31c5744e25be92a64e7f77beb', 'If you reached this screen by accident and meant to visit one of your own sites, here are some shortcuts to help you find your way.', NULL, NULL, 'LINE', NULL, 10, '', '08cbe037e394e47b4dc740a5ae61ce70', NULL, ''),
(5887, 'en', 'default', '12c31c3cb7ab49899ec2e979d5a553f5', 'If you temporarily make your %s file writable for us to generate rewrite rules automatically, do not forget to revert the permissions after rule has been saved.', NULL, NULL, 'LINE', NULL, 10, '', 'fbfb20c605ce87bf0b18c0013694071e', NULL, ''),
(5888, 'en', 'default', 'a98ef57dff34e18723548ccd8a286c59', 'If you temporarily make your site&#8217;s root directory writable for us to generate the %s file automatically, do not forget to revert the permissions after the file has been created.', NULL, NULL, 'LINE', NULL, 10, '', '55f26516c6be022a2bae7e814fdb1559', NULL, ''),
(5889, 'en', 'default', 'efe517a5b918a12ecb7dfd17f3877327', 'If you want site visitors to be able to register themselves, as opposed to by the site administrator, check the membership box. A default user role can be set for all new users, whether self-registered or registered by the site admin.', NULL, NULL, 'LINE', NULL, 10, '', '1e0104cd42ac1e2fe540ba466d5ff248', NULL, ''),
(5890, 'en', 'default', 'e8953b095643b4bf6a1cd0bae7a85508', 'If you want to convert your categories to tags (or vice versa), use the <a href=\"%s\">Categories and Tags Converter</a> available from the Import screen.', NULL, NULL, 'LINE', NULL, 10, '', '194c0e92da833182de4233fc3786e2b7', NULL, ''),
(5891, 'en', 'default', 'be3d2742b2dbbb1878b22b8702b6e8d7', 'If you want to install a plugin that you&#8217;ve downloaded elsewhere, click the Upload Plugin button above the plugins list. You will be prompted to upload the .zip package, and once uploaded, you can activate the new plugin.', NULL, NULL, 'LINE', NULL, 10, '', '313943fe8cc1a3d691aa966a6a6819c7', NULL, ''),
(5892, 'en', 'default', '0b5d8aeeb0c74419d4028dca5dc744df', 'If you want to make changes but don&#8217;t want them to be overwritten when the plugin is updated, you may be ready to think about writing your own plugin. For information on how to edit plugins, write your own from scratch, or just better understand their anatomy, check out the links below.', NULL, NULL, 'LINE', NULL, 10, '', '8c4742c33256ee314435258e3a200070', NULL, ''),
(5893, 'en', 'default', 'fc276f47f9e88e53fb9a81e0e683a057', 'If you want to remove the widget but save its setting for possible future use, just drag it into the Inactive Widgets area. You can add them back anytime from there. This is especially helpful when you switch to a theme with fewer or different widget areas.', NULL, NULL, 'LINE', NULL, 10, '', 'f5f6f5978f1a37ee120927289f3f6f8d', NULL, ''),
(5894, 'en', 'default', 'b54fa9afe2dc888d8a5c71cc1b61b51c', 'If you want to run multiple WordPress installations in a single database, change this.', NULL, NULL, 'LINE', NULL, 10, '', '7ed16a5d29814e6517b04e35979dbb8c', NULL, ''),
(5895, 'en', 'default', 'ce99e8b4ddb1935ad216af170d465663', 'If you would like to see more plugins to choose from, click on the &#8220;Add New&#8221; button and you will be able to browse or search for additional plugins from the <a href=\"%s\">WordPress Plugin Directory</a>. Plugins in the WordPress Plugin Directory are designed and developed by third parties, and are compatible with the license WordPress uses. Oh, and they&#8217;re free!', NULL, NULL, 'LINE', NULL, 10, '', '918021f3c1c7333bbb985975acdf69b3', NULL, ''),
(5896, 'en', 'default', 'ff1e9b8584871613da97bf909bf0d3d4', 'If you would like to see more themes to choose from, click on the &#8220;Add New&#8221; button and you will be able to browse or search for additional themes from the <a href=\"%s\">WordPress Theme Directory</a>. Themes in the WordPress Theme Directory are designed and developed by third parties, and are compatible with the license WordPress uses. Oh, and they&#8217;re free!', NULL, NULL, 'LINE', NULL, 10, '', '85b7f40dae14e2dedd2d051b8da068b4', NULL, ''),
(5897, 'en', 'default', '5074f6b262894aae8f6923d7ff9eadf1', 'If your %1$s file was <a href=\"%2$s\">writable</a>, we could do this automatically, but it isn&#8217;t so these are the mod_rewrite rules you should have in your %1$s file. Click in the field and press %3$s to select all.', NULL, NULL, 'LINE', NULL, 10, '', '0651b69befec7bf871bf4d593b4eb1cf', NULL, ''),
(5898, 'en', 'default', '93f3cd2b6a8fda8223f25e6fd0f47db4', 'If your %1$s file was <a href=\"%2$s\">writable</a>, we could do this automatically, but it isn&#8217;t so this is the url rewrite rule you should have in your %1$s file. Click in the field and press %3$s to select all. Then insert this rule inside of the %4$s element in %1$s file.', NULL, NULL, 'LINE', NULL, 10, '', '4768687268baff9591b17ec71c06e3d6', NULL, ''),
(5899, 'en', 'default', 'ae4333ffddf970f98ea2387193cc0f5b', 'If your theme has more than one default header image, or you have uploaded more than one custom header image, you have the option of having WordPress display a randomly different image on each page of your site. Click the &#8220;Random&#8221; radio button next to the Uploaded Images or Default Images section to enable this feature.', NULL, NULL, 'LINE', NULL, 10, '', 'aad695b974325b8e604c6a490dbf8754', NULL, ''),
