(5930, 'en', 'default', '911b598e98c0d31c7e5bdfd6e1136dbc', 'In all likelihood, these items were supplied to you by your Web Host. If you don&#8217;t have this information, then you will need to contact them before you can continue. If you&#8217;re all ready&hellip;', NULL, NULL, 'LINE', NULL, 10, '', '01afe2a4a10214c1f8f359b9da7a694c', NULL, ''),
(5931, 'en', 'default', '72247007ca8dd9d5b747cd2d13855ae4', 'In most cases, WordPress will automatically apply maintenance and security updates in the background for you.', NULL, NULL, 'LINE', NULL, 10, '', 'f608b0ae3b2099cd13950879df8872a3', NULL, ''),
(5932, 'en', 'default', '0278248c794c7dd11a47837d5861ee71', 'In previous versions of WordPress, all importers were built-in. They have been turned into plugins since most people only use them once or infrequently.', NULL, NULL, 'LINE', NULL, 10, '', '43ae0cedba6e9504ba24d8691432a3a9', NULL, ''),
(5933, 'en', 'default', '5c43aa88fc70f0b190b05aafdc6ba900', 'In reply to %s.', NULL, NULL, 'LINE', NULL, 10, '', '9ec9ce045b44687da826fd1655ccc31e', NULL, ''),
