(7013, 'en', 'default', 'cd61e834ef28051bae99e31d131c100a', 'Welcome to an improved Customizer workflow with design drafts, locking, scheduling, and preview links. What&#8217;s more, code syntax highlighting and error checking will make for a clean and smooth site building experience. Finally, if all that wasn&#8217;t pretty great, we&#8217;ve got a great new Gallery widget and improvements to theme browsing and switching.', NULL, NULL, 'LINE', NULL, 10, '', 'dc333a927a553863a9e96af78504f8ac', NULL, ''),
(7014, 'en', 'default', '1edbf8934455e35e01018196f1aa0a7f', 'Welcome to the famous five-minute WordPress installation process! Just fill in the information below and you&#8217;ll be on your way to using the most extendable and powerful personal publishing platform in the world.', NULL, NULL, 'LINE', NULL, 10, '', 'c8ed12874ebd60ce751ed1d79fa1a1fb', NULL, ''),
(7015, 'en', 'default', '7bb5b05b47216f59eeab4b23d0014277', 'Welcome to your WordPress Dashboard! This is the screen you will see when you log in to your site, and gives you access to all the site management features of WordPress. You can get help for any screen by clicking the Help tab above the screen title.', NULL, NULL, 'LINE', NULL, 10, '', 'caa3ada644015d7da497c61e78d64e63', NULL, ''),
