(7040, 'en', 'default', '5f71bda8d746f5a38f8be090639b5e2f', 'Widgets are independent sections of content that can be placed into any widgetized area provided by your theme (commonly called sidebars). To populate your sidebars/widget areas with individual widgets, drag and drop the title bars into the desired area. By default, only the first widget area is expanded. To populate additional widget areas, click on their title bars to expand them.', NULL, NULL, 'LINE', NULL, 10, '', '2628ac04367ef5339322e23a38240fb5', NULL, ''),
(7041, 'en', 'default', '2a1b56a60634250cd6cf205c74ed00c6', 'Widgets may be used multiple times. You can give each widget a title, to display on your site, but it&#8217;s not required.', NULL, NULL, 'LINE', NULL, 10, '', 'cb26d608dcaf5e53bfdccbb076ee7f6b', NULL, ''),
