(7080, 'en', 'default', 'ee6b36df0a0e5ae29804573f7bc0f5ec', 'Yes, take me to my profile page', NULL, NULL, 'LINE', NULL, 10, '', '1d3042fbf321b0f29a212ba1fa41b065', NULL, ''),
(7081, 'en', 'default', '59f603400b805b1de2d465999a14a8d1', 'Yes, you read that right. Just like you can draft and revise posts and schedule them to go live on the date and time you choose, you can now tinker with your site&#8217;s design and schedule those design changes to go live as you please.', NULL, NULL, 'LINE', NULL, 10, '', '92a26f1aeae59b581679ae5b9662ea6d', NULL, ''),
(7082, 'en', 'default', '7cff9b6df2ec3f0734a9bfa274ad5b71', 'You also have some plugins or themes with updates available. Update them now:', NULL, NULL, 'LINE', NULL, 10, '', '2b582a384405ef134b2e3732c6752c15', NULL, ''),
(7083, 'en', 'default', 'f8ba38900d10b5b21aee002e9e7bd51b', 'You appear to be making direct edits to your plugin in the WordPress dashboard. We recommend that you don&#8217;t! Editing plugins directly may introduce incompatibilities that break your site and your changes may be lost in future updates.', NULL, NULL, 'LINE', NULL, 10, '', 'f826278325752cd0128cabb05d582280', NULL, ''),
(7084, 'en', 'default', '8cc087cec19b11374357e1838a97fdd2', 'You appear to be making direct edits to your theme in the WordPress dashboard. We recommend that you don&#8217;t! Editing your theme directly could break your site and your changes may be lost in future updates. If you need to tweak more than your theme&#8217;s CSS, you might want to try <a href=\"%s\">making a child theme</a>.', NULL, NULL, 'LINE', NULL, 10, '', 'b9f6841fdabf7c589997152eee94c053', NULL, ''),
(7085, 'en', 'default', 'f8c6ee5e73552f569602296fefb93e97', 'You are about to approve the following comment:', NULL, NULL, 'LINE', NULL, 10, '', 'a741669e2308fc1b7849c6cea467d4a4', NULL, ''),
(7086, 'en', 'default', '2de0cde3d4f7c41a74f39952dfd47c53', 'You are about to delete %s.', NULL, NULL, 'LINE', NULL, 10, '', '561dcb4033e66c90b80111cb744268f2', NULL, ''),
(7087, 'en', 'default', '5c81416e5ec54afcce8585081aebfaa2', 'You are about to delete the following comment:', NULL, NULL, 'LINE', NULL, 10, '', '9774a607e28a78ded0ebe7bb2d32c603', NULL, ''),
(7088, 'en', 'default', 'dffbb3fce635732b0e6620dd590f61f9', 'You are about to delete this link \'%s\'\\n  \'Cancel\' to stop, \'OK\' to delete.', NULL, NULL, 'LINE', NULL, 10, '', 'a7ed45b50d0811bbee71533027965055', NULL, ''),
(7089, 'en', 'default', 'ac4f38ab41fe32062fad243041ab44ad', 'You are about to delete this theme \'%s\'\\n  \'Cancel\' to stop, \'OK\' to delete.', NULL, NULL, 'LINE', NULL, 10, '', 'b49de564fe6352d019b2c60f0bf6aaa3', NULL, ''),
(7090, 'en', 'default', '0232103929f5d403f6c7958a3b807a8d', 'You are about to install WordPress %s <strong>in English (US).</strong> There is a chance this update will break your translation. You may prefer to wait for the localized version to be released.', NULL, NULL, 'LINE', NULL, 10, '', '157295ad1a6686eb670ef26a04b03842', NULL, ''),
(7091, 'en', 'default', '3b4ff5a4b188de3878909bd3a9b3cec8', 'You are about to mark the following comment as spam:', NULL, NULL, 'LINE', NULL, 10, '', 'c16df9f52a96623f2edb6aac33a659b1', NULL, ''),
(7092, 'en', 'default', 'b24c6934170aa498626a6144d6515933', 'You are about to move the following comment to the Trash:', NULL, NULL, 'LINE', NULL, 10, '', '7eb50bef1f149a97a512a17b3860c94a', NULL, ''),
(7093, 'en', 'default', '67ebe7a24df35d5ec7ed817c5842a194', 'You are about to remove the following plugin:', NULL, NULL, 'LINE', NULL, 10, '', 'ea9e1c9dd74972fc87e537c0b8a75783', NULL, ''),
(7094, 'en', 'default', 'bbad7ae088e6c0da4561965a1c369358', 'You are about to remove the following plugins:', NULL, NULL, 'LINE', NULL, 10, '', '5c3fe5012767405bbfe2446333b20717', NULL, ''),
(7095, 'en', 'default', '5e2e56330bcbe7fad88b2f4f103f7ddb', 'You are currently editing the page that shows your latest posts.', NULL, NULL, 'LINE', NULL, 10, '', '8e7076e0d8e43c940232b9e797f28ddf', NULL, ''),
(7096, 'en', 'default', '96cef5b3d6d082373acf8e547cf63ed4', 'You are now logged out everywhere else.', NULL, NULL, 'LINE', NULL, 10, '', 'eaa09b45dbf6e5106e26f1575642c8a7', NULL, ''),
(7097, 'en', 'default', 'f6435aa05732baca22a6de8167d3bfd8', 'You are only logged in at this location.', NULL, NULL, 'LINE', NULL, 10, '', '6efa0e36e3fa8fa35878b98eefa724f7', NULL, ''),
(7098, 'en', 'default', 'a2ffc66c941814a9d5b0bb75664a1438', 'You are using a development version (%1$s). Cool! Please <a href=\"%2$s\">stay updated</a>.', NULL, NULL, 'LINE', NULL, 10, '', '1611f33e667d8986d6e5af86e2632027', NULL, ''),
(7099, 'en', 'default', 'ead95faf097c050ce8272235ab6d14c4', 'You are using a development version of WordPress. These feature plugins are also under development. <a href=\"%s\">Learn more</a>.', NULL, NULL, 'LINE', NULL, 10, '', 'c6341544fa2046eec14107fc5e0c2991', NULL, ''),
(7100, 'en', 'default', 'cd695c6558c271b3503ba79fa724359b', 'You are using a development version of WordPress. You can update to the latest nightly build automatically:', NULL, NULL, 'LINE', NULL, 10, '', '2da8caa967c5d80d5b3f918eb28683a1', NULL, ''),
(7101, 'en', 'default', '0f823bed6cba9b8049dc4eecf9eee287', 'You are using an insecure browser!', NULL, NULL, 'LINE', NULL, 10, '', '23213d53b58d52a73b5c6b791e0751da', NULL, ''),
(7102, 'en', 'default', 'ffa32f3198455426db78715a26184f3d', 'You are using the browser&#8217;s built-in file uploader. The WordPress uploader includes multiple file selection and drag and drop capability. <a href=\"#\">Switch to the multi-file uploader</a>.', NULL, NULL, 'LINE', NULL, 10, '', '3762a3858f871b91761c7c17e352fc8d', NULL, ''),
(7103, 'en', 'default', '185a58071513be42df712fa7aa59c054', 'You are using the multi-file uploader. Problems? Try the <a href=\"%1$s\" target=\"%2$s\">browser uploader</a> instead.', NULL, NULL, 'LINE', NULL, 10, '', 'acee86659ffcf9ad8390cef00f6496bb', NULL, ''),
(7104, 'en', 'default', '313fdf221f4a041ed3d772112928e653', 'You attempted to access the \"%1$s\" dashboard, but you do not currently have privileges on this site. If you believe you should be able to access the \"%1$s\" dashboard, please contact your network administrator.', NULL, NULL, 'LINE', NULL, 10, '', '020de39e8d89520db671b4fe148e7915', NULL, ''),
(7105, 'en', 'default', '7f735a3de4fa14b113264f459aa23a72', 'You attempted to edit an attachment that doesn&#8217;t exist. Perhaps it was deleted?', NULL, NULL, 'LINE', NULL, 10, '', '08496ed3c471ff8f386f2bfad6e83a98', NULL, ''),
(7106, 'en', 'default', '30ea891c72c225103705a6d5327fb6da', 'You attempted to edit an item that doesn&#8217;t exist. Perhaps it was deleted?', NULL, NULL, 'LINE', NULL, 10, '', '258ba72a61465a83c608f7bbcb04e28f', NULL, ''),
(7107, 'en', 'default', '63e10978f6765868b1d450b854d82fc7', 'You attempted to edit an item that isn&#8217;t an attachment. Please go back and try again.', NULL, NULL, 'LINE', NULL, 10, '', '900cefb2779b90dfcb085ea912988321', NULL, ''),
(7108, 'en', 'default', '1597e47943552acb2159f24763d2183b', 'You can Search for themes by keyword, author, or tag, or can get more specific and search by criteria listed in the feature filter.', NULL, NULL, 'LINE', NULL, 10, '', 'eb4979e5ab1b2ae278daadd1702a0603', NULL, '');
INSERT INTO `arch_icl_strings` (`id`, `language`, `context`, `name`, `value`, `string_package_id`, `location`, `type`, `title`, `status`, `gettext_context`, `domain_name_context_md5`, `word_count`, `translation_priority`) VALUES
(7109, 'en', 'default', 'a028caf874f7a36b4d04977846a44352', 'You can Upload a theme manually if you have already downloaded its ZIP archive onto your computer (make sure it is from a trusted and original source). You can also do it the old-fashioned way and copy a downloaded theme&#8217;s folder via FTP into your %s directory.', NULL, NULL, 'LINE', NULL, 10, '', '333d26c9fe96a9e26648cc1908869146', NULL, ''),
(7110, 'en', 'default', '5d8dccaf5152dadcf683142ff8e39d85', 'You can add links here to be displayed on your site, usually using <a href=\"%s\">Widgets</a>. By default, links to several sites in the WordPress community are included as examples.', NULL, NULL, 'LINE', NULL, 10, '', 'b76bb7ad7b1732e2160f2f83dbe88287', NULL, ''),
(7111, 'en', 'default', '68219ec1373a07050daabcab0c978815', 'You can add or edit links on this screen by entering information in each of the boxes. Only the link&#8217;s web address and name (the text you want to display on your site as the link) are required fields.', NULL, NULL, 'LINE', NULL, 10, '', '6912f31d5f004663b4c8e4ad15c73082', NULL, ''),
(7112, 'en', 'default', 'b816582da0fe96d33555b82e7cf35504', 'You can also browse a user&#8217;s favorite plugins, by using the Favorites link above the plugins list and entering their WordPress.org username.', NULL, NULL, 'LINE', NULL, 10, '', '96e13d80dd4d210f6e818be399382890', NULL, ''),
(7113, 'en', 'default', '4f2592dccb64c180a81e4a042fe70670', 'You can also choose a background color by clicking the Select Color button and either typing in a legitimate HTML hex value, e.g. &#8220;#ff0000&#8221; for red, or by choosing a color using the color picker.', NULL, NULL, 'LINE', NULL, 10, '', 'b7e503868ca5ca70400ee20723cb8c47', NULL, ''),
(7114, 'en', 'default', '6b872e2b359ab19726c69cfc6662980b', 'You can also control the display of your content in RSS feeds, including the maximum number of posts to display and whether to show full text or a summary.', NULL, NULL, 'LINE', NULL, 10, '', 'ac7ebf12f6abef2a950abe5280a54102', NULL, ''),
(7115, 'en', 'default', '464b4a78bca01186db0bc144f178c5f1', 'You can also create posts with the <a href=\"%s\">Press This bookmarklet</a>.', NULL, NULL, 'LINE', NULL, 10, '', 'd0528ae0c11e8490f10c23327b15cafe', NULL, ''),
(7116, 'en', 'default', '972d412b5c2482e96d24171ef27553c5', 'You can also delete individual items and access the extended edit screen from the details dialog.', NULL, NULL, 'LINE', NULL, 10, '', '2e77a6c303195f810df4934f2989f298', NULL, ''),
(7117, 'en', 'default', 'c83a2faddec053e93996bea49df84dd8', 'You can also edit or move multiple posts to the trash at once. Select the posts you want to act on using the checkboxes, then select the action you want to take from the Bulk Actions menu and click Apply.', NULL, NULL, 'LINE', NULL, 10, '', '95e6f8b591af95b7e141df1318db45fb', NULL, ''),
(7118, 'en', 'default', '5860a8fa3ef1ea4cf6657c47ed83e90c', 'You can also embed media from many popular websites including Twitter, YouTube, Flickr and others by pasting the media URL on its own line into the content of your post/page. Please refer to the Codex to <a href=\"https://codex.wordpress.org/Embeds\">learn more about embeds</a>.', NULL, NULL, 'LINE', NULL, 10, '', 'c214466f990742e589dfd33015c4c203', NULL, ''),
(7119, 'en', 'default', '001ae354418426ceb96e7d035ad9de3c', 'You can also moderate the comment from this screen using the Status box, where you can also change the timestamp of the comment.', NULL, NULL, 'LINE', NULL, 10, '', '8c34b8c1eaba94f7289ab3289a5399fe', NULL, ''),
(7120, 'en', 'default', '653290ee830033b040967d621084cb0f', 'You can also perform the same types of actions, including narrowing the list by using the filters, acting on a page using the action links that appear when you hover over a row, or using the Bulk Actions menu to edit the metadata for multiple pages at once.', NULL, NULL, 'LINE', NULL, 10, '', 'de3f01588a23e6930c757cb640155dfa', NULL, ''),
(7121, 'en', 'default', 'ff2945d6f4371f51df1c739ba60f27ae', 'You can assign keywords to your posts using <strong>tags</strong>. Unlike categories, tags have no hierarchy, meaning there&#8217;s no relationship from one tag to another.', NULL, NULL, 'LINE', NULL, 10, '', 'df93416910d6e5d402da0166617b2ee4', NULL, ''),
(7122, 'en', 'default', '72caeed1ff140d706e86977a0bf7d12c', 'You can assign theme locations to individual menus by <strong>selecting the desired settings</strong> at the bottom of the menu editor. To assign menus to all theme locations at once, <strong>visit the Manage Locations tab</strong> at the top of the screen.', NULL, NULL, 'LINE', NULL, 10, '', '9362d167fdacec13de259ffccbd99596', NULL, ''),
(7123, 'en', 'default', 'ceb79a4cdaccc9c24ea22489ba03429f', 'You can change the display of this screen using the Screen Options tab to set how many items are displayed per screen and to display/hide columns in the table.', NULL, NULL, 'LINE', NULL, 10, '', '59cf444b48715208ad794a188fdb27a3', NULL, ''),
(7124, 'en', 'default', 'aa6b50ebfd26013f727b5bddd1a0020a', 'You can change your password, turn on keyboard shortcuts, change the color scheme of your WordPress administration screens, and turn off the WYSIWYG (Visual) editor, among other things. You can hide the Toolbar (formerly called the Admin Bar) from the front end of your site, however it cannot be disabled on the admin screens.', NULL, NULL, 'LINE', NULL, 10, '', 'b9b4d0532a778cafb41ef6adf422c685', NULL, ''),
(7125, 'en', 'default', 'ed3a1364a6f32d0152a9a84b1badda00', 'You can change your profile picture on <a href=\"%s\">Gravatar</a>.', NULL, NULL, 'LINE', NULL, 10, '', 'c6a264b0634dfa17c05df0e6129f3f73', NULL, ''),
(7126, 'en', 'default', '3a94690f89aea910c66bdb3b6fff1fc0', 'You can choose from the theme&#8217;s default header images, or use one of your own. You can also customize how your Site Title and Tagline are displayed.', NULL, NULL, 'LINE', NULL, 10, '', 'a9f5169f4cc9dff9335f02dc21036a92', NULL, ''),
(7127, 'en', 'default', '60a664b6a8f309a460918c0a26f8a6c1', 'You can choose one of your previously uploaded headers, or show a random one.', NULL, NULL, 'LINE', NULL, 10, '', 'd47efcdc40344fa4bb4ed32631d75492', NULL, ''),
(7128, 'en', 'default', 'f84196c911ec5a65c48ccee53f33c850', 'You can choose what&#8217;s displayed on the homepage of your site. It can be posts in reverse chronological order (classic blog), or a fixed/static page. To set a static homepage, you first need to create two <a href=\"%s\">Pages</a>. One will become the homepage, and the other will be where your posts are displayed.', NULL, NULL, 'LINE', NULL, 10, '', '649280ffbec104f0b86be27e09164287', NULL, ''),
(7129, 'en', 'default', '0754b49868a7a0ac641085882e136d75', 'You can choose whether or not your site will be crawled by robots, ping services, and spiders. If you want those services to ignore your site, click the checkbox next to &#8220;Discourage search engines from indexing this site&#8221; and click the Save Changes button at the bottom of the screen. Note that your privacy is not complete; your site is still visible on the web.', NULL, NULL, 'LINE', NULL, 10, '', '1051f547d613d166e58ee2267ea6046e', NULL, ''),
(7130, 'en', 'default', '4e171722a615bf0a18d4a10f47f71464', 'You can create groups of links by using Link Categories. Link Category names must be unique and Link Categories are separate from the categories you use for posts.', NULL, NULL, 'LINE', NULL, 10, '', '4b54c6d68320d2f035fccf562e2bab97', NULL, ''),
(7131, 'en', 'default', 'b37e44bc690a283009213e84f746d497', 'You can create the %s file manually and paste the following text into it.', NULL, NULL, 'LINE', NULL, 10, '', '76f4b68a6e9d1fcc894f1beae939aebe', NULL, ''),
(7132, 'en', 'default', '3f1914dd8eecd9de86a33cddc8389cbb', 'You can customize the display of this screen in a number of ways:', NULL, NULL, 'LINE', NULL, 10, '', '75b6f3ea46162ff1ee094bb0533bebb6', NULL, ''),
(7133, 'en', 'default', 'd6ea257fc3b6e7e133f43b11581d331a', 'You can customize the display of this screen using the Screen Options tab and/or the dropdown filters above the links table.', NULL, NULL, 'LINE', NULL, 10, '', 'e7d15f9c59ea3e6ee7f40b3c1e031594', NULL, ''),
(7134, 'en', 'default', '8ac8e55c35195400fff6756b49835523', 'You can customize the display of this screen&#8217;s contents in a number of ways:', NULL, NULL, 'LINE', NULL, 10, '', '7ed2cb9c4a7f52993516725cd5168e1c', NULL, ''),
(7135, 'en', 'default', 'ea5fcb3e59f97c7e70111b588d78d687', 'You can customize the look of your site without touching any of your theme&#8217;s code by using a custom background. Your background can be an image or a color.', NULL, NULL, 'LINE', NULL, 10, '', 'b39450760894637b67668aae44c27c96', NULL, ''),
(7136, 'en', 'default', '5aaf5b7354c090ad1a53700fdd8fe022', 'You can delete Link Categories in the Bulk Action pull-down, but that action does not delete the links within the category. Instead, it moves them to the default Link Category.', NULL, NULL, 'LINE', NULL, 10, '', 'b8541d8f687d12a898125b597c36ca87', NULL, ''),
(7137, 'en', 'default', '319980b189e432ee59526496b2fc7bd7', 'You can edit the image while preserving the thumbnail. For example, you may wish to have a square thumbnail that displays just a section of the image.', NULL, NULL, 'LINE', NULL, 10, '', '84a98079b9b86c1c74f975e3e854fa1b', NULL, ''),
(7138, 'en', 'default', '29a977dfe4c91dbbb1970d8a38a81a2a', 'You can edit the information left in a comment if needed. This is often useful when you notice that a commenter has made a typographical error.', NULL, NULL, 'LINE', NULL, 10, '', '995b8ae48206399e7a9530a8154fbbd3', NULL, ''),
(7139, 'en', 'default', 'd5fb0f5d059e20b79f51ba2cb67696b2', 'You can enable distraction-free writing mode using the icon to the right. This feature is not available for old browsers or devices with small screens, and requires that the full-height editor be enabled in Screen Options.', NULL, NULL, 'LINE', NULL, 10, '', 'dda7292d6cc6432d85981a7dcd8632bc', NULL, ''),
(7140, 'en', 'default', '439e85bf8c00d0e5d97a3f08a503f1ce', 'You can export a file of your site&#8217;s content in order to import it into another installation or platform. The export file will be an XML file format called WXR. Posts, pages, comments, custom fields, categories, and tags can be included. You can choose for the WXR file to include only certain posts or pages by setting the dropdown filters to limit the export by category, author, date range by month, or publishing status.', NULL, NULL, 'LINE', NULL, 10, '', '9588f209116dc30d63fdf43bb35e6897', NULL, ''),
(7141, 'en', 'default', '893b004b3b255507cc8437e21994eadb', 'You can filter the list of posts by post status using the text links above the posts list to only show posts with that status. The default view is to show all posts.', NULL, NULL, 'LINE', NULL, 10, '', '014d2d802fc8f834f23987d4b037d30a', NULL, ''),
(7142, 'en', 'default', '336a3ba49f4fd88e0d9f10b69511d0df', 'You can filter the list of users by User Role using the text links above the users list to show All, Administrator, Editor, Author, Contributor, or Subscriber. The default view is to show all users. Unused User Roles are not listed.', NULL, NULL, 'LINE', NULL, 10, '', '8653fbcfa867f1c95e2d3b856e2d8b47', NULL, ''),
(7143, 'en', 'default', '415205a59aff75e660caf73215b1dce4', 'You can find additional themes for your site by using the Theme Browser/Installer on this screen, which will display themes from the <a href=\"%s\">WordPress Theme Directory</a>. These themes are designed and developed by third parties, are available free of charge, and are compatible with the license WordPress uses.', NULL, NULL, 'LINE', NULL, 10, '', 'b7b2dd7c837caf0e94c8104888321a73', NULL, ''),
(7144, 'en', 'default', '2a4f1cc2e99d292a9132ee9cfb9c3b26', 'You can find new plugins to install by searching or browsing the directory right here in your own Plugins section.', NULL, NULL, 'LINE', NULL, 10, '', 'f21a09c0e4a3b3eee841ef6b410151fa', NULL, ''),
(7145, 'en', 'default', 'd6e863468c3551518fa205b1060b3cf3', 'You can hide/display columns based on your needs and decide how many posts to list per screen using the Screen Options tab.', NULL, NULL, 'LINE', NULL, 10, '', '767d49624cffb7efeb180fe4bdd5e07a', NULL, ''),
(7146, 'en', 'default', '67e7c46d3d5c2108c0e99ceea6441dee', 'You can hide/display columns based on your needs and decide how many users to list per screen using the Screen Options tab.', NULL, NULL, 'LINE', NULL, 10, '', '48e4f7cb4f7122f5b317b5c99b0da496', NULL, ''),
(7147, 'en', 'default', 'de8768b94597e0280a0f1177f2e46900', 'You can insert media files by clicking the icons above the post editor and following the directions. You can align or edit images using the inline formatting toolbar available in Visual mode.', NULL, NULL, 'LINE', NULL, 10, '', 'c495912a27038b213ad535fd59f27db5', NULL, ''),
(7148, 'en', 'default', 'b9ee24b2ed698a7774a132f859a7440b', 'You can log out of other devices, such as your phone or a public computer, by clicking the Log Out Everywhere Else button.', NULL, NULL, 'LINE', NULL, 10, '', '1b241a88804ad03a64423db084510037', NULL, ''),
(7149, 'en', 'default', '74ec803b504414d42126006142f54490', 'You can manage comments made on your site similar to the way you manage posts and other content. This screen is customizable in the same ways as other management screens, and you can act on comments using the on-hover action links or the Bulk Actions.', NULL, NULL, 'LINE', NULL, 10, '', '054e0c1e1a79152711e5105dee4b047f', NULL, ''),
(7150, 'en', 'default', '82169939daa05d25d68df1a2d649d306', 'You can narrow the list by file type/status or by date using the dropdown menus above the media table.', NULL, NULL, 'LINE', NULL, 10, '', 'db58d14d2240eb7c402d9fbd316f6e77', NULL, ''),
(7151, 'en', 'default', 'ae310675793fc7bfa950b8760f79a4f1', 'You can now manage and live-preview Custom Backgrounds in the <a href=\"%1$s\">Customizer</a>.', NULL, NULL, 'LINE', NULL, 10, '', '7cb915125bad49f15eac14767efbd6ce', NULL, ''),
(7152, 'en', 'default', '5c71e77cf2c930baea8c287753a229c2', 'You can now manage and live-preview Custom Header in the <a href=\"%1$s\">Customizer</a>.', NULL, NULL, 'LINE', NULL, 10, '', 'a7882a3b8d2131be6d89949cc08d58da', NULL, ''),
(7153, 'en', 'default', '82deca0fb59319be08a217a38bcd7d23', 'You can proportionally scale the original image. For best results, scaling should be done before you crop, flip, or rotate. Images can only be scaled down, not up.', NULL, NULL, 'LINE', NULL, 10, '', 'f8773edde68d4c1d2cea0cef4a3554d0', NULL, ''),
(7154, 'en', 'default', 'a4e5684835acb802ee3194c309c7ed1a', 'You can refine the list to show only posts in a specific category or from a specific month by using the dropdown menus above the posts list. Click the Filter button after making your selection. You also can refine the list by clicking on the post author, category or tag in the posts list.', NULL, NULL, 'LINE', NULL, 10, '', '392e9b74ecaedf4daa058dcc790516c4', NULL, ''),
