(5166, 'en', 'default', '4219e9a017987428685266b8afb91d80', '<code>_blank</code> &mdash; new window or tab.', NULL, NULL, 'LINE', NULL, 10, '', '6afc5273e6815c3d46e0ebdb4a13b4d5'),
(5167, 'en', 'default', '54664b363fac4157930a3f35531b297a', '<code>_none</code> &mdash; same window or tab.', NULL, NULL, 'LINE', NULL, 10, '', 'cba77215afd0b5d8e96ef1a01273f4d0'),
(5168, 'en', 'default', 'd0e80edbec66288a957ad0381ae03505', '<code>_top</code> &mdash; current window or tab, with no frames.', NULL, NULL, 'LINE', NULL, 10, '', 'eacc121dfd228bdf407e10172ae29704'),
(5169, 'en', 'default', '747ebf74423d5239b4023bc329eaf353', '<span>Active:</span> %s', NULL, NULL, 'LINE', NULL, 10, '', '14fb66009c7a8b6f0cf5e1ded911823b'),
(5170, 'en', 'default', '2e9457fe36435e48a296c4a614e71a4f', '<strong>%s</strong> &mdash; This allows you to associate an image with your post without inserting it. This is usually useful only if your theme makes use of the image as a post thumbnail on the home page, a custom header, etc.', NULL, NULL, 'LINE', NULL, 10, '', '3006d7934608817a28a556e753065609'),
(5171, 'en', 'default', 'f067f6f2a07a9f28b8acd051de12bd8f', '<strong><em>Note that password</em></strong> carefully! It is a <em>random</em> password that was generated just for you.', NULL, NULL, 'LINE', NULL, 10, '', '667f6a0fa877d898323aca1fc507a99e'),
(5172, 'en', 'default', 'ecdb52fb00d5aabe4e9d24b436f9b9fc', '<strong>Activity</strong> &mdash; Shows the upcoming scheduled posts, recently published posts, and the most recent comments on your posts and allows you to moderate them.', NULL, NULL, 'LINE', NULL, 10, '', '92492a427dfb72cd1b97719e8e35e896'),
(5173, 'en', 'default', 'a6babfcb475f676a839ed6c0640d1a7d', '<strong>Advice:</strong> think very carefully about your site crashing if you are live-editing the theme currently in use.', NULL, NULL, 'LINE', NULL, 10, '', 'ad4647305c60cfd93de5e674fc745759'),
(5174, 'en', 'default', 'a8f4d14621490c3a1b94015a6744f684', '<strong>At A Glance</strong> &mdash; Displays a summary of the content on your site and identifies which theme and version of WordPress you are using.', NULL, NULL, 'LINE', NULL, 10, '', '63cb0e79249a3955ef8698cb0ff6b575'),
(5175, 'en', 'default', 'b65fa8d75f92ea10c226c9c9e107cfc6', '<strong>Box Controls</strong> &mdash; Click the title bar of the box to expand or collapse it. Some boxes added by plugins may have configurable content, and will show a &#8220;Configure&#8221; link in the title bar if you hover over it.', NULL, NULL, 'LINE', NULL, 10, '', '54545fe38eda999a7a67927af2e0e559'),
(5176, 'en', 'default', '2bc2b92ef21e54b7d57719af3e03b906', '<strong>Clicking the arrow to the right of any menu item</strong> in the editor will reveal a standard group of settings. Additional settings such as link target, CSS classes, link relationships, and link descriptions can be enabled and disabled via the Screen Options tab.', NULL, NULL, 'LINE', NULL, 10, '', '44adfce5db156b9d67e7c275453a89b9'),
(5177, 'en', 'default', 'b1223bca091660d2cb26c4403f354ea0', '<strong>Compatible</strong> with your version of WordPress', NULL, NULL, 'LINE', NULL, 10, '', '96c7d1203469aa395da152a984342fe8'),
(5178, 'en', 'default', '019360949341be40f9abc62e0d947806', '<strong>Connection lost.</strong> Saving has been disabled until you&#8217;re reconnected.', NULL, NULL, 'LINE', NULL, 10, '', 'c7ad62cbc8e82263e1ecdbb66fe5fb52'),
(5179, 'en', 'default', '009d122ea5b480829aa49478250882f2', '<strong>Delete</strong> brings you to the Delete Users screen for confirmation, where you can permanently remove a user from your site and delete their content. You can also delete multiple users at once by using Bulk Actions.', NULL, NULL, 'LINE', NULL, 10, '', '6f347940b95b62848846a02047a935e5'),
(5180, 'en', 'default', '455ea6f84b4f4e0349d551dbb50d3a42', '<strong>Description</strong> &mdash; The description is not prominent by default; however, some themes may display it.', NULL, NULL, 'LINE', NULL, 10, '', 'bf0e83818cfea660dd6082860a6da784'),
(5181, 'en', 'default', '72a914bd12c78373873416f57b033b13', '<strong>Discussion</strong> &mdash; You can turn comments and pings on or off, and if there are comments on the post, you can see them here and moderate them.', NULL, NULL, 'LINE', NULL, 10, '', 'feb178d959aae886b2dde85248492051'),
(5182, 'en', 'default', '71ce1121b4c5579d8f2c8e71335b9a22', '<strong>Drag and Drop</strong> &mdash; To rearrange the boxes, drag and drop by clicking on the title bar of the selected box and releasing when you see a gray dotted-line rectangle appear in the location you want to place the box.', NULL, NULL, 'LINE', NULL, 10, '', '5152a8dbc58f8c3531ec3b414be417aa'),
(5183, 'en', 'default', 'eb233701305e7a3f2c7906f8fc65e817', '<strong>Drag and drop</strong> your files into the area below. Multiple files are allowed.', NULL, NULL, 'LINE', NULL, 10, '', '8edfe8913a0e716cd74f3f38806c7700'),
(5184, 'en', 'default', 'f62a6b681a08721ae6b0f640c11373e9', '<strong>ERROR:</strong> There was an error connecting to the server, Please verify the settings are correct.', NULL, NULL, 'LINE', NULL, 10, '', '4de9a113fcc64567a124a76f81341274'),
(5185, 'en', 'default', '7d150f25e6563473ebc64666237efb54', '<strong>ERROR</strong>: \"Table Prefix\" is invalid.', NULL, NULL, 'LINE', NULL, 10, '', 'a8b0f7cfdec390a2a79211fa262fccd6'),
(5186, 'en', 'default', '881815006609d34e07215728019253f8', '<strong>ERROR</strong>: Passwords may not contain the character \"\\\".', NULL, NULL, 'LINE', NULL, 10, '', 'b8a94917e225393c809afc968a530c56'),
