(7059, 'en', 'default', '920b86d822cd25a2a2c592134fd08ce1', 'WordPress grows when people like you tell their friends about it, and the thousands of businesses and services that are built on and around WordPress share that fact with their users. We&#8217;re flattered every time someone spreads the good word, just make sure to <a href=\"%s\">check out our trademark guidelines</a> first.', NULL, NULL, 'LINE', NULL, 10, '', '84d9b5767192db384ce1c4e4cddb9c27'),
(7060, 'en', 'default', 'ca2e1c6fdebc7887415465aaaf360960', 'WordPress has been installed. Thank you, and enjoy!', NULL, NULL, 'LINE', NULL, 10, '', '1745013426dd7b654f51d4fb2a69e0b2'),
