(7185, 'en', 'default', 'af16ea89802a72d2349dd2cf9f154203', 'You cannot update because <a href=\"https://codex.wordpress.org/Version_%1$s\">WordPress %1$s</a> requires PHP version %2$s or higher and MySQL version %3$s or higher. You are running PHP version %4$s and MySQL version %5$s.', NULL, NULL, 'LINE', NULL, 10, '', 'a49b9412aa1ef33a1cf20ae3d5d93282'),
(7186, 'en', 'default', '9d3950bfbf1f532636941e6e7472d526', 'You cannot update because <a href=\"https://codex.wordpress.org/Version_%1$s\">WordPress %1$s</a> requires PHP version %2$s or higher. You are running version %3$s.', NULL, NULL, 'LINE', NULL, 10, '', 'fa47aa31c394af7be58786ff71d047c1'),
(7187, 'en', 'default', 'a34c26b14e4cc0bbbc7539acf0fd8c13', 'You did not enter a category name.', NULL, NULL, 'LINE', NULL, 10, '', 'bf8173990f84489006dd9def7d3eaa40'),
(7188, 'en', 'default', 'e15fb7addf8eb9749136275482f556bf', 'You have %1$s and %2$s.', NULL, NULL, 'LINE', NULL, 10, '', 'b2418cd4bfe76e0ffedcf84970b8d4d7'),
(7189, 'en', 'default', '7167162991d8800ebce7e1bd624574d9', 'You have access to the source code, the freedom to study how the program works, and the freedom to change it to make it do what you wish.', NULL, NULL, 'LINE', NULL, 10, '', '67c789df58149c9af13602cb20315029'),
(7190, 'en', 'default', '231c8ae8e02bd4937e66b0fa0b9588ad', 'You have chosen to delete the following users from all networks and sites.', NULL, NULL, 'LINE', NULL, 10, '', '743cf80abb6d03cae3640471aabe3491'),
(7191, 'en', 'default', '50ba57aa7975ab51d7c025e4b23bb478', 'You have chosen to delete the user from all networks and sites.', NULL, NULL, 'LINE', NULL, 10, '', '4f76bb390c3e3e0eee0c6ad4b850feac'),
(7192, 'en', 'default', '3ffd956536e1922c7f5fdb831039af4d', 'You have specified these users for deletion:', NULL, NULL, 'LINE', NULL, 10, '', '6655504129856a6b11475fc5bcd5fddb'),
(7193, 'en', 'default', 'dcf56f1f987777a64b0797a6280c7630', 'You have specified these users for removal:', NULL, NULL, 'LINE', NULL, 10, '', '1db9520cb37b952cfab013eafa7de642'),
(7194, 'en', 'default', 'd45015d783e12de12c64715139082a95', 'You have specified this user for deletion:', NULL, NULL, 'LINE', NULL, 10, '', '1a2452c1c335bc83f1fe02980cc3c685'),
(7195, 'en', 'default', 'cecaf3df13302d167b563c6f2c980397', 'You have specified this user for removal:', NULL, NULL, 'LINE', NULL, 10, '', '8d377aebf8e8feeef80e9b285767ba0d'),
(7196, 'en', 'default', 'ebe4c21683e008d862e4e5f729a203de', 'You have the freedom to distribute copies of your modified versions to others. By doing this you can give the whole community a chance to benefit from your changes.', NULL, NULL, 'LINE', NULL, 10, '', '575f6f63a84e77e779c6b4bbbf3e52fd'),
(7197, 'en', 'default', 'd0b1d308c36a91f47ad5e072aaae6a4f', 'You have the freedom to redistribute copies of the original program so you can help your neighbor.', NULL, NULL, 'LINE', NULL, 10, '', '5302e7318dff38138b16c74bec41ac75'),
(7198, 'en', 'default', '7a7e94999871e055f0ea180174c53e92', 'You have the freedom to run the program, for any purpose.', NULL, NULL, 'LINE', NULL, 10, '', '6f38ef688ea848435c5323c1050764c9'),
(7199, 'en', 'default', '5e1d755e43145fcf50b50f74de799f9d', 'You have the latest version of WordPress.', NULL, NULL, 'LINE', NULL, 10, '', 'ff2d031a36ae6ec0c3e8a4fe8fffee07'),
(7200, 'en', 'default', '7f1c2f39ed3c50fe87e16573b59088b6', 'You have used your space quota. Please delete files before uploading.', NULL, NULL, 'LINE', NULL, 10, '', 'ed4a19ef0c23deed3aeeb0e082e95ce9'),
(7201, 'en', 'default', '910de734a93033bfae5c872ab94014de', 'You have version %1$s installed. Update to %2$s.', NULL, NULL, 'LINE', NULL, 10, '', '0a67da94779d8b09e9a300e81f18970e'),
(7202, 'en', 'default', '9afc94ea9920155044857e2a6f58559a', 'You may also browse based on the most popular tags in the Plugin Directory:', NULL, NULL, 'LINE', NULL, 10, '', 'b266bcecfb6fce486a0191acd42470dc'),
(7203, 'en', 'default', 'b9209ebd71e334a6b5c114b956bd04ba', 'You must be a member of at least one site to use this page.', NULL, NULL, 'LINE', NULL, 10, '', '138a858bcaed698543f788e5f8bd74d4'),
(7204, 'en', 'default', '84760e95a02e1b21fd067dcf8d357fdc', 'You must click the Save Changes button at the bottom of the screen for new settings to take effect.', NULL, NULL, 'LINE', NULL, 10, '', '40de51e1c0d646fdf0d7d9f4efaa3ea7'),
(7205, 'en', 'default', '8c2bf6c76b2e996aee15001305864362', 'You must provide a domain name.', NULL, NULL, 'LINE', NULL, 10, '', '1322fae1121e7cb69cf04cd8b740e787'),
(7206, 'en', 'default', 'e8730e81bbcd3c1a192571908f8ccbd0', 'You must provide a name for your network of sites.', NULL, NULL, 'LINE', NULL, 10, '', '38318c699d2ad2c069b095ab3c904a84'),
(7207, 'en', 'default', '5d91af26a811fea9f83a2c345f142333', 'You must provide a valid email address.', NULL, NULL, 'LINE', NULL, 10, '', 'daccce93d18aa9636599502c2635414c'),
(7208, 'en', 'default', 'd21ee1e755e1f20f741241633e4c2c94', 'You must provide an email address.', NULL, NULL, 'LINE', NULL, 10, '', 'cee4081dcafb607585d9b25919ba4e82'),
(7209, 'en', 'default', '7c2217673b6d04c1a90bf975b9819f61', 'You need JavaScript to choose a part of the image.', NULL, NULL, 'LINE', NULL, 10, '', 'b2a19886e07bbcedf751944a51ba2c01'),
(7210, 'en', 'default', '4d24d578a5927f1b57cca938c9634429', 'You need to make this file writable before you can save your changes. See <a href=\"https://codex.wordpress.org/Changing_File_Permissions\">the Codex</a> for more information.', NULL, NULL, 'LINE', NULL, 10, '', '23f5f43ef597a9ca39b41090de27cbcf'),
(7211, 'en', 'default', 'beaf0116620161dfd235826de23b0914', 'You only have one theme enabled for this site right now. Visit the Network Admin to <a href=\"%1$s\">enable</a> more themes.', NULL, NULL, 'LINE', NULL, 10, '', 'bec1eb11fa82b83008bddf617573b1fa'),
(7212, 'en', 'default', '6188c977a07d60341263e3468f63fc53', 'You only have one theme enabled for this site right now. Visit the Network Admin to <a href=\"%1$s\">enable</a> or <a href=\"%2$s\">install</a> more themes.', NULL, NULL, 'LINE', NULL, 10, '', '074021ed5d224492b983c728b5a36337'),
(7213, 'en', 'default', '4fead004d501ab5fe7fdf10f1ce81066', 'You only have one theme installed right now. Live a little! You can choose from over 1,000 free themes in the WordPress Theme Directory at any time: just click on the <a href=\"%s\">Install Themes</a> tab above.', NULL, NULL, 'LINE', NULL, 10, '', '650eb45a7cbc5f876ba3d67795cae07a'),
(7214, 'en', 'default', 'e48d877ffe90d1093eff68a0bca8fede', 'You should be able to get this info from your web host, if %s doesn&#8217;t work.', NULL, NULL, 'LINE', NULL, 10, '', '3e79998aef2fa2408a3b32f68d5a471a'),
(7215, 'en', 'default', 'fad179aacb874bd889126c37df44e895', 'You should update your .htaccess now.', NULL, NULL, 'LINE', NULL, 10, '', 'e8ae3b21778fe2f1c8d2ff97bf0889b2'),
