(4946, 5166, 'pl', 0, NULL, '<code>_blank</code> &mdash; nowe okno lub karta.', NULL, '', 0, '2018-05-15 08:04:50'),
(4947, 5167, 'pl', 0, NULL, '<code>_none</code> &mdash; to samo okno lub karta.', NULL, '', 0, '2018-05-15 08:04:50'),
