(5005, 5225, 'pl', 0, NULL, 'W <strong>wersji %1$s</strong> naprawiono błąd w zabezpieczeniach i %2$s inne błędy.', NULL, '', 0, '2018-05-15 08:04:50'),
(5006, 5226, 'pl', 0, NULL, 'W <strong>wersji %1$s</strong> naprawiono pewne błędy w zabezpieczeniach i %2$s inny błąd.', NULL, '', 0, '2018-05-15 08:04:50'),
